async function fetchIniFile(url) {
  try {
    const response = await fetch(url);
    const langues = await response.json();
    for (i = 0; i < langues.length; i++) {
      const Language = langues[i].Language;
      const NomFichier = './niovelius_' + Language + '.ini';
      try {
        const testexiste = await fetch(NomFichier);
        const chaines = await testexiste.json();
        prez.object('imgDrapeau' + Language).show();
      } catch (error) {
        prez.object('imgDrapeau' + Language).hide();
        console.error('fichier non trouvé ou non json : ' + NomFichier);
      } 
    }
  } catch (error) {
    console.error('fichier non trouvé ou pas json');
    console.error(error);
  }
}
