async function fetchTextFile(url) {
  try {
    const response = await fetch(url);
    const chaines = await response.json();
    for (i=0; i < chaines.length; i++)
    {
        prez.variable(chaines[i].Name, chaines[i].Value);
    }
    return true;
  } catch (error) {
    console.error(error);
    return false;
  }
}
